import React, { useState, useEffect } from 'react';
import MenuBar from './MenuBar';
import { Map, TileLayer, Tooltip, Circle, Polyline } from 'react-leaflet';
import { Button } from 'semantic-ui-react';

const InteractiveMap = (props) => {
    const initialLat = 1.462251; // Fallback latitude
    const initialLng = 110.413307; // Fallback longitude

    // State to control visibility of lines and store fetched data
    const [hideLine, setHideLine] = useState(false);
    const [data, setData] = useState([]);
    const [mapCenter, setMapCenter] = useState([initialLat, initialLng]); // State to control map center

    // Extracting case_id from props passed via routing
    const { case_id } = (props.location && props.location.state) || {};


    useEffect(() => {
        // URL to fetch data based on case_id
        const url = `http://mozzhub.fcsit.unimas.my/mozzhubAPI/mobMapData/${case_id}`;

        async function fetchData() {
            try {
                const response = await fetch(url);
                if (!response.ok) {
                    throw new Error('Network response was not ok ' + response.statusText);
                }
                const data = await response.json();
                // Store fetched data in state
                setData(data);
                if (data.length > 0) {
                    // Update map center to the first data point's location
                    setMapCenter([data[0].lat, data[0].lng]);
                }
            } catch (error) {
                console.error('There was a problem with the fetch operation:', error);
            }
        }

        fetchData();
    }, [case_id]); // Dependency array includes case_id to re-fetch if it changes

    console.log(case_id, "AAAAAA")
    console.log(data.lat, "API Fetch")

    // Toggle function to hide/show lines
    const handleClick = () => {
        setHideLine(!hideLine);
    }

    // Function to handle Close button click
    const handleCloseButton = () => {
        this.porps.history.push('/record'); // Navigate to '/record' route
    }

    const controlPoints = []; // Array to store control points for curves

    // Calculate control points for polyline curves
    for (let i = 0; i < data.length - 1; i++) {
        const p1 = [data[i].lat, data[i].lng];
        const p2 = [data[i + 1].lat, data[i + 1].lng];

        const controlPoint = [
            (p1[0] + p2[0]) / 2, // Midpoint of latitudes
            (p1[1] + p2[1]) / 2, // Midpoint of longitudes
        ];

        controlPoints.push(controlPoint);
    }

    return (
        <div style={{ backgroundColor: 'grey', width: '100%' }}>
            <MenuBar style={{marginTop: '0px'}}/>
            <div>
            <Map center={mapCenter} zoom={13} style={{ width: '100%', height: '560px', zIndex: '0' }}>
                <TileLayer
                    attribution="&amp;copy <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    maxZoom={16}
                    minZoom={2}
                />
                {data.map((elem, i) => {
                    var l = (0.9 - data[i].ranking * 0.9) * 255;
                    var h = (0.9 - data[i].ranking * 0.9) * 240;
                    var a = data[i].ranking * 0.9;
                    var rgb = "rgb(255," + l + "," + l + ")";
                    return (
                        <Circle
                            key={i}
                            center={{ lat: data[i].lat, lng: data[i].lng }}
                            color='red'
                            fillColor='red'
                            fillOpacity=''
                            radius={50}
                            onClick={handleClick} // Toggle lines on circle click
                        >
                            <Tooltip style={{ maxWidth: "150px", wordWrap: "break-word" }}>
                                <h3>Case ID: {data[i].Case_id}</h3>
                                <p>Location: {data[i].Location}</p>
                            </Tooltip>
                        </Circle>
                    )
                })}
                {/* Render curved lines between circles */}
                {!hideLine && data.length > 1 && controlPoints.map((controlPoint, i) => (
                    <Polyline
                        key={i}
                        positions={[
                            [data[i].lat, data[i].lng], // Start point
                            controlPoint, // Control point
                            [data[i + 1].lat, data[i + 1].lng], // End point
                        ]}
                        color="blue"
                    />
                ))}
            </Map>
            <div style={{ display: 'flex', flexDirection: 'column', marginLeft: '10px' }}>
                <div style={{ margin: "2px", border: "2px solid black" }}>
                    <p style={{ margin: "2px" }}>Map Legend:</p>
                    <p style={{ margin: "2px" }}>Circle: Data points</p>
                    <p style={{ margin: "2px" }}>Case ID:</p>
                </div>
                <div style={{ position: "absolute", right: "10px" }}>
                    <Button style={{ marginTop: "10px" }} onClick={handleCloseButton}>Close</Button>
                </div>
            </div>
            </div>
        </div>
    )
}

export default InteractiveMap;
