import React from 'react';
import { Map, TileLayer, Tooltip, Circle, Polyline } from 'react-leaflet';

const Heatmap = ({ lat, lng, ranking = [] }) => {
    const controlPoints = []; // Array to store control points for curves

    const dummyRanking = [
        { lat: 51.5074, lng: -0.1278, ranking: 0.8 }, // London
        { lat: 40.7128, lng: -74.0060, ranking: 0.6 }, // New York
        { lat: 34.0522, lng: -118.2437, ranking: 0.4 }, // Los Angeles
        { lat: 41.8781, lng: -87.6298, ranking: 0.5 }, // Chicago
        { lat: 37.7749, lng: -122.4194, ranking: 0.7 } // San Francisco
    ];
    

    // Calculate control points
    for (let i = 0; i < ranking.length - 1; i++) {
        const p1 = [ranking[i].lat, ranking[i].lng];
        const p2 = [ranking[i + 1].lat, ranking[i + 1].lng];

        const controlPoint = [
            (p1[0] + p2[0]) / 2, // Midpoint of latitudes
            (p1[1] + p2[1]) / 2, // Midpoint of longitudes
        ];

        controlPoints.push(controlPoint);
    }

    return (
        <Map center={[lat, lng]} zoom={13} style={{ width: '100%', height: '820px', margin: 'auto' }}>
            <TileLayer
                attribution="&amp;copy <a href=&quot;http://osm.org/copyright&quot;>OpenStreetMap</a> contributors"
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                maxZoom={16}
                minZoom={6}
            />
            {ranking.map((elem, i) => {
                var l = (0.9 - ranking[i].ranking * 0.9) * 255;
                var h = (0.9 - ranking[i].ranking * 0.9) * 240;
                var a = ranking[i].ranking * 0.9;
                var rgb = "rgb(255," + l + "," + l + ")";
                return (
                    <Circle
                        key={i}
                        center={{ lat: ranking[i].lat, lng: ranking[i].lng }}
                        color='red'
                        fillColor={rgb}
                        fillOpacity={a}
                        radius={400}>
                        <Tooltip><h3>Ranking:#{i + 1}</h3></Tooltip>
                    </Circle>
                )
            })}
            {/* Render curved lines between circles */}
            {ranking.length > 1 && controlPoints.map((controlPoint, i) => (
                <Polyline
                    key={i}
                    positions={[
                        [ranking[i].lat, ranking[i].lng], // Start point
                        controlPoint, // Control point
                        [ranking[i + 1].lat, ranking[i + 1].lng], // End point
                    ]}
                    color="blue"
                />
            ))}
        </Map>
    )
}

export default Heatmap;
